SELECT record_time, SUM( add_diamond ) AS totalDiamond, SUM( order_diamond ) AS totalOrderDiamond FROM tbl_record_spread_diamond_add WHERE 1 = 1
and record_time <= #{queryDate, jdbcType=DATE}
GROUP BY record_time ORDER BY record_time DESC LIMIT 0,30

